import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Profile() {
    const [user, setUser] = useState(null);
    const [error, setError] = useState(null);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isLogin, setIsLogin] = useState(true);
    const [successMessage, setSuccessMessage] = useState('');
    const [showLessonForm, setShowLessonForm] = useState(false); // Управление показом формы урока
    const [lessonData, setLessonData] = useState({
      title: '',
      description: '',
      language: '',
      difficulty_level: '',
      content: {
        text_fragments: [{ text: '', translation: '' }],
        audio: [{ file: '', description: '' }],
        video: [{ file: '', description: '' }],
        pictures: [{ file: '', description: '' }],
      },
      vocabulary: [{ word: '', translation: '' }],
      quiz: [{ question: '', options: ['', '', '', ''], correct_answer: 0 }],
    });
    const navigate = useNavigate();
  
    useEffect(() => {
      const token = localStorage.getItem('token');
      if (token) {
        fetchProfile(token);
      }
    }, []);
  
    const fetchProfile = async (token) => {
      try {
        const response = await axios.get('http://localhost:5000/profile', {
          headers: {
            Authorization: token,
          },
        });
        setUser(response.data);
      } catch (err) {
        console.error('Ошибка при загрузке профиля:', err.response?.data || err.message);
        setError('Ошибка при загрузке данных профиля');
      }
    };
  
    const handleLogin = async (e) => {
      e.preventDefault();
  
      try {
        const response = await axios.post('http://localhost:5000/login', { email, password });
        const { token } = response.data;
        localStorage.setItem('token', token);
        setSuccessMessage('Ура, вы успешно вошли!');
        setTimeout(() => {
          navigate('/profile');
        }, 2000);
      } catch (err) {
        setError('Неверный логин или пароль');
      }
    };
  
    const handleLogout = () => {
      localStorage.removeItem('token');
      setUser(null);
      setSuccessMessage('');
    };
  
    const handleCreateLesson = () => {
      setShowLessonForm(true); // Открыть форму для создания урока
    };
  
    const handleSaveLesson = async (e) => {
      e.preventDefault();
      try {
        const token = localStorage.getItem('token');
        await axios.post(
          'http://localhost:5000/lessons',
          lessonData,
          {
            headers: {
              Authorization: token,
            },
          }
        );
        alert('Урок успешно создан!');
        setLessonData({
          title: '',
          description: '',
          language: '',
          difficulty_level: '',
          content: {
            text_fragments: [{ text: '', translation: '' }],
            audio: [{ file: '', description: '' }],
            video: [{ file: '', description: '' }],
            pictures: [{ file: '', description: '' }],
          },
          vocabulary: [{ word: '', translation: '' }],
          quiz: [{ question: '', options: ['', '', '', ''], correct_answer: 0 }],
        });
        setShowLessonForm(false);
      } catch (err) {
        console.error('Ошибка при создании урока:', err.response?.data || err.message);
        alert('Ошибка при создании урока');
      }
    };
  
    const handleLessonInputChange = (e) => {
      const { name, value } = e.target;
      setLessonData({ ...lessonData, [name]: value });
    };
  
    const handleInputChangeInArray = (path, index, field, value) => {
      const keys = path.split('.');
      let target = lessonData;
      for (let key of keys) target = target[key];
      target[index][field] = value;
      setLessonData({ ...lessonData });
    };
  
    const handleAddItem = (path) => {
      const keys = path.split('.');
      let target = lessonData;
      for (let key of keys.slice(0, -1)) target = target[key];
      target[keys[keys.length - 1]].push({ text: '', translation: '' });
      setLessonData({ ...lessonData });
    };
  
    const handleRemoveItem = (path, index) => {
      const keys = path.split('.');
      let target = lessonData;
      for (let key of keys.slice(0, -1)) target = target[key];
      target[keys[keys.length - 1]].splice(index, 1);
      setLessonData({ ...lessonData });
    };
  
    if (user) {
      return (
        <div>
          <h1>Добро пожаловать, {user.name || 'Пользователь'}</h1>
          <p>Email: {user.email}</p>
          <p>Дата регистрации: {user.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'Неизвестно'}</p>
          <button onClick={handleLogout}>Выйти</button>
          <button onClick={handleCreateLesson}>Создать урок</button>
  
          {showLessonForm && (
            <form onSubmit={handleSaveLesson}>
              <h2>Добавить урок</h2>
              <input
                type="text"
                name="title"
                placeholder="Название урока"
                value={lessonData.title}
                onChange={handleLessonInputChange}
                required
              />
              <textarea
                name="description"
                placeholder="Описание урока"
                value={lessonData.description}
                onChange={handleLessonInputChange}
                required
              ></textarea>
              <input
                type="text"
                name="language"
                placeholder="Язык"
                value={lessonData.language}
                onChange={handleLessonInputChange}
                required
              />
              <input
                type="text"
                name="difficulty_level"
                placeholder="Уровень сложности"
                value={lessonData.difficulty_level}
                onChange={handleLessonInputChange}
                required
              />
  
              <h3>Текстовые фрагменты</h3>
              {lessonData.content.text_fragments.map((fragment, index) => (
                <div key={index}>
                  <input
                    type="text"
                    placeholder="Текст"
                    value={fragment.text}
                    onChange={(e) =>
                      handleInputChangeInArray('content.text_fragments', index, 'text', e.target.value)
                    }
                  />
                  <input
                    type="text"
                    placeholder="Перевод"
                    value={fragment.translation}
                    onChange={(e) =>
                      handleInputChangeInArray('content.text_fragments', index, 'translation', e.target.value)
                    }
                  />
                  <button type="button" onClick={() => handleRemoveItem('content.text_fragments', index)}>Удалить</button>
                </div>
              ))}
              <button type="button" onClick={() => handleAddItem('content.text_fragments')}>Добавить фрагмент</button>
  
              {/* Аналогично добавьте поля для audio, video, pictures, vocabulary и quiz */}
              <button type="submit">Сохранить урок</button>
              <button type="button" onClick={() => setShowLessonForm(false)}>Отмена</button>
            </form>
          )}
        </div>
      );
    }
  
    return (
      <div>
        <h1>{isLogin ? 'Вход' : 'Регистрация'}</h1>
        <form onSubmit={isLogin ? handleLogin : handleRegister}>
          <div>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <input
              type="password"
              placeholder="Пароль"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">{isLogin ? 'Войти' : 'Зарегистрироваться'}</button>
        </form>
        <p>
          {isLogin ? 'Нет аккаунта?' : 'Есть аккаунт?'}{' '}
          <button onClick={() => setIsLogin(!isLogin)}>
            {isLogin ? 'Зарегистрироваться' : 'Войти'}
          </button>
        </p>
        {successMessage && <p>{successMessage}</p>}
        {error && <p>{error}</p>}
      </div>
    );
  }
  
  export default Profile;
