import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Profile() {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true);  // Флаг для переключения между входом и регистрацией
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchProfile(token);
    }
  }, []);

  const fetchProfile = async () => {
    try {
        const token = localStorage.getItem('token'); // Получаем токен из localStorage (или другого хранилища)
        if (!token) {
            throw new Error('Токен отсутствует. Необходима авторизация.');
        }

        const response = await axios.get('http://localhost:5000/profile', {
            headers: {
                Authorization: token, // Передача токена в заголовке
            },
        });

        console.log('Профиль загружен:', response.data);
        return response.data;
    } catch (err) {
        console.error('Ошибка при загрузке профиля:', err.response?.data || err.message);
        throw err;
    }
};


const handleLogin = async () => {
    try {
        const response = await axios.post('http://localhost:5000/login', { email, password });
        const { token } = response.data;

        localStorage.setItem('token', token); // Сохранение токена
        console.log('Успешный вход. Токен сохранён.');
    } catch (err) {
        console.error('Ошибка при входе:', err.response?.data || err.message);
    }
};

  const handleRegister = async () => {
    try {
        const response = await axios.post('http://localhost:5000/register', {
            email,
            password,
        });
        console.log('Успешная регистрация:', response.data); // Лог успешного ответа
    } catch (err) {
        console.error('Ошибка при регистрации:', err.response?.data || err.message); // Лог ошибки
    }
};



  const handleLogout = () => {
    // Удаляем токен из localStorage при выходе
    localStorage.removeItem('token');
    setUser(null);
  };

  if (user) {
    // Если пользователь авторизован, показываем его профиль
    return (
      <div>
        <h1>Добро пожаловать, {user.name}</h1>
        <p>Email: {user.email}</p>
        <p>Дата регистрации: {new Date(user.createdAt).toLocaleDateString()}</p>
        <button onClick={handleLogout}>Выйти</button>
      </div>
    );
  }

  // Если пользователь не авторизован, показываем форму для входа/регистрации
  return (
    <div>
      <h1>{isLogin ? 'Вход' : 'Регистрация'}</h1>

      <form onSubmit={isLogin ? handleLogin : handleRegister}>
        <div>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">{isLogin ? 'Войти' : 'Зарегистрироваться'}</button>
      </form>

      <p>
        {isLogin ? 'Нет аккаунта?' : 'Есть аккаунт?'}{' '}
        <button onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? 'Зарегистрироваться' : 'Войти'}
        </button>
      </p>

      {error && <p>{error}</p>}
    </div>
  );
}

export default Profile;
