import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function LessonEdit() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [lesson, setLesson] = useState({
    title: '',
    description: '',
    language: '',
    difficulty_level: '',
    content: {
      text_fragments: [{ text: '', translation: '' }],
      audio: [{ file: '', description: '' }],
      video: [{ file: '', description: '' }],
      pictures: [{ file: '', description: '' }],
    },
    vocabulary: [{ word: '', translation: '' }],
    quiz: [{ question: '', options: ['', '', '', ''], correct_answer: 0 }],
  });

  useEffect(() => {
    const fetchLesson = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/lessons/${id}`);
        
        // Инициализация массивов с пустыми значениями, если они отсутствуют
        const data = response.data;
        setLesson({
          ...data,
          content: {
            ...data.content,
            audio: data.content.audio || [{ file: '', description: '' }],
            video: data.content.video || [{ file: '', description: '' }],
            pictures: data.content.pictures || [{ file: '', description: '' }],
          },
        });
      } catch (err) {
        console.error("Ошибка при загрузке урока для редактирования:", err);
      }
    };
    fetchLesson();
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLesson({ ...lesson, [name]: value });
  };

  const handleTextFragmentChange = (index, e) => {
    const { name, value } = e.target;
    const updatedFragments = [...lesson.content.text_fragments];
    updatedFragments[index][name] = value;
    setLesson({ ...lesson, content: { ...lesson.content, text_fragments: updatedFragments } });
  };

  const handleFileChange = (e, type, index) => {
    const file = e.target.files[0];

    // Проверяем существование поля и массива
    if (lesson.content[type] && Array.isArray(lesson.content[type]) && lesson.content[type][index]) {
      const updatedContent = { ...lesson.content };
      updatedContent[type][index].file = file;
      setLesson({ ...lesson, content: updatedContent });
    } else {
      console.error(`Массив ${type} не существует или элемент с индексом ${index} не найден`);
    }
  };

  const handleAddTextFragment = () => {
    setLesson({
      ...lesson,
      content: {
        ...lesson.content,
        text_fragments: [...lesson.content.text_fragments, { text: '', translation: '' }],
      },
    });
  };

  const handleAddFile = (type) => {
    // Проверяем, существует ли массив, если нет — инициализируем его
    const updatedContent = { ...lesson.content };
    updatedContent[type] = updatedContent[type] || [];

    // Добавляем новый объект в соответствующий массив
    updatedContent[type].push({ description: '', file: null });

    setLesson({
      ...lesson,
      content: updatedContent,
    });
  };

  const handleAddWord = () => {
    setLesson({
      ...lesson,
      vocabulary: [...lesson.vocabulary, { word: '', translation: '' }],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Вы не авторизованы');
        return;
      }

      const formData = new FormData();
      formData.append('title', lesson.title);
      formData.append('description', lesson.description);
      formData.append('language', lesson.language);
      formData.append('difficulty_level', lesson.difficulty_level);

      // Добавляем текстовые фрагменты
      lesson.content.text_fragments.forEach((fragment, index) => {
        formData.append(`text_fragments[${index}][text]`, fragment.text);
        formData.append(`text_fragments[${index}][translation]`, fragment.translation);
      });

      // Обработка файлов (аудио, видео, фотографии)
      const addFiles = (type) => {
        if (lesson.content[type]) {
          lesson.content[type].forEach((item, index) => {
            if (item.file) {
              formData.append(`${type}[${index}]`, item.file);
            }
            formData.append(`${type}[${index}]_description`, item.description);
          });
        }
      };

      addFiles('audio');
      addFiles('video');
      addFiles('pictures');

      // Добавляем словарь
      lesson.vocabulary.forEach((word, index) => {
        formData.append(`vocabulary[${index}][word]`, word.word);
        formData.append(`vocabulary[${index}][translation]`, word.translation);
      });

      await axios.put(`http://localhost:5000/lessons/${id}`, formData, {
        headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' },
      });

      alert('Урок успешно обновлен!');
      navigate(`/lessons/${id}`); // Перенаправление на страницу урока
    } catch (err) {
      console.error('Ошибка при обновлении урока:', err);
      alert('Ошибка при обновлении урока');
    }
  };

  return (
    <div className="lesson-edit">
      <h1>Редактировать урок</h1>
      <form onSubmit={handleSubmit}>
        {/* Основная информация */}
        <div>
          <label>Название</label>
          <input
            type="text"
            name="title"
            value={lesson.title}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Описание</label>
          <textarea
            name="description"
            value={lesson.description}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Язык</label>
          <select
            name="language"
            value={lesson.language}
            onChange={handleInputChange}
          >
            <option value="es">Испанский</option>
            <option value="en">Английский</option>
          </select>
        </div>
        <div>
          <label>Уровень сложности</label>
          <select
            name="difficulty_level"
            value={lesson.difficulty_level}
            onChange={handleInputChange}
          >
            <option value="beginner">Начальный</option>
            <option value="intermediate">Средний</option>
            <option value="advanced">Сложный</option>
          </select>
        </div>

        {/* Текстовые фрагменты */}
        <div>
          <h2>Текстовые фрагменты</h2>
          {lesson.content.text_fragments.map((fragment, index) => (
            <div key={index}>
              <label>Текст</label>
              <input
                type="text"
                name="text"
                value={fragment.text}
                onChange={(e) => handleTextFragmentChange(index, e)}
              />
              <label>Перевод</label>
              <input
                type="text"
                name="translation"
                value={fragment.translation}
                onChange={(e) => handleTextFragmentChange(index, e)}
              />
            </div>
          ))}
          <button type="button" onClick={handleAddTextFragment}>
            Добавить текстовый фрагмент
          </button>
        </div>

        {/* Аудиофайлы */}
        <div>
          <h2>Аудиофайлы</h2>
          {lesson.content.audio.map((audio, index) => (
            <div key={index}>
              <label>Описание</label>
              <input
                type="text"
                name="description"
                value={audio.description}
                onChange={(e) => handleFileChange(e, 'audio', index)}
              />
              <label>Аудиофайл</label>
              <input
                type="file"
                onChange={(e) => handleFileChange(e, 'audio', index)}
              />
            </div>
          ))}
          <button type="button" onClick={() => handleAddFile('audio')}>
            Добавить аудиофайл
          </button>
        </div>

        {/* Видеофайлы */}
        <div>
          <h2>Видеофайлы</h2>
          {lesson.content.video.map((video, index) => (
            <div key={index}>
              <label>Описание</label>
              <input
                type="text"
                name="description"
                value={video.description}
                onChange={(e) => handleFileChange(e, 'video', index)}
              />
              <label>Видеофайл</label>
              <input
                type="file"
                onChange={(e) => handleFileChange(e, 'video', index)}
              />
            </div>
          ))}
          <button type="button" onClick={() => handleAddFile('video')}>
            Добавить видеоролик
          </button>
        </div>

        {/* Фотографии */}
        <div>
          <h2>Фотографии</h2>
          {lesson.content.pictures.map((picture, index) => (
            <div key={index}>
              <label>Описание</label>
              <input
                type="text"
                name="description"
                value={picture.description}
                onChange={(e) => handleFileChange(e, 'pictures', index)}
              />
              <label>Фотография</label>
              <input
                type="file"
                onChange={(e) => handleFileChange(e, 'pictures', index)}
              />
            </div>
          ))}
          <button type="button" onClick={() => handleAddFile('pictures')}>
            Добавить фотографию
          </button>
        </div>

        {/* Словарь */}
        <div>
          <h2>Словарь</h2>
          {lesson.vocabulary.map((word, index) => (
            <div key={index}>
              <label>Слово</label>
              <input
                type="text"
                name="word"
                value={word.word}
                onChange={(e) => handleVocabularyChange(index, e)}
              />
              <label>Перевод</label>
              <input
                type="text"
                name="translation"
                value={word.translation}
                onChange={(e) => handleVocabularyChange(index, e)}
              />
            </div>
          ))}
          <button type="button" onClick={handleAddWord}>
            Добавить слово
          </button>
        </div>

        <button type="submit">Сохранить изменения</button>
      </form>
    </div>
  );
}

export default LessonEdit;
