import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Для получения параметров из URL
import './LessonDetails.css';

const LessonDetails = () => {
  const { id } = useParams(); // Получаем ID урока из URL
  const [lesson, setLesson] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/lesson/${id}`) // Получаем урок по ID
      .then((response) => {
        if (!response.ok) {
          throw new Error('Урок не найден');
        }
        return response.json();
      })
      .then((data) => {
        setLesson(data); // Сохраняем данные урока
      })
      .catch((error) => {
        setError(error.message); // Обработка ошибки
      });
  }, [id]);

  return (
    <div className="lesson-details">
      {error && <p className="error-message">{error}</p>}
      {lesson ? (
        <div>
          <h1>{lesson.title}</h1>
          <p><strong>Описание:</strong> {lesson.description}</p>
          <p><strong>Язык:</strong> {lesson.language}</p>
          <p><strong>Уровень сложности:</strong> {lesson.difficulty_level}</p>
          <h3>Содержание:</h3>
          <p>{lesson.content}</p>
          <h3>Важные слова:</h3>
          <ul>
            {lesson.vocabulary && lesson.vocabulary.map((word, index) => (
              <li key={index}>
                <strong>{word.word}:</strong> {word.translation}
              </li>
            ))}
          </ul>
          <h3>Тест:</h3>
          <ul>
            {lesson.quiz && lesson.quiz.map((question, index) => (
              <li key={index}>
                {question.question}
                <ul>
                  {question.answers.map((answer, idx) => (
                    <li key={idx}>{answer}</li>
                  ))}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Загрузка данных урока...</p>
      )}
    </div>
  );
};

export default LessonDetails;
