import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Profile() {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true);  // Флаг для переключения между входом и регистрацией
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchProfile(token);
    }
  }, []);

  const fetchProfile = async (token) => {
    try {
      const response = await axios.get('/profile', {
        headers: {
          Authorization: token,  // Передаем токен в заголовке
        },
      });
      setUser(response.data);  // Получаем данные пользователя
    } catch (err) {
      setError('Ошибка при загрузке профиля');
      console.error(err);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/login', { email, password });
      const { token } = response.data;

      // Сохраняем токен в localStorage
      localStorage.setItem('token', token);

      // Перенаправляем на страницу профиля
      fetchProfile(token);
    } catch (err) {
      setError('Неверный логин или пароль');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/register', { email, password });
      const { token } = response.data;

      // Сохраняем токен в localStorage
      localStorage.setItem('token', token);

      // Перенаправляем на страницу профиля
      fetchProfile(token);
    } catch (err) {
      setError('Ошибка при регистрации');
    }
  };

  const handleLogout = () => {
    // Удаляем токен из localStorage при выходе
    localStorage.removeItem('token');
    setUser(null);
  };

  if (user) {
    // Если пользователь авторизован, показываем его профиль
    return (
      <div>
        <h1>Добро пожаловать, {user.name}</h1>
        <p>Email: {user.email}</p>
        <p>Дата регистрации: {new Date(user.createdAt).toLocaleDateString()}</p>
        <button onClick={handleLogout}>Выйти</button>
      </div>
    );
  }

  // Если пользователь не авторизован, показываем форму для входа/регистрации
  return (
    <div>
      <h1>{isLogin ? 'Вход' : 'Регистрация'}</h1>

      <form onSubmit={isLogin ? handleLogin : handleRegister}>
        <div>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">{isLogin ? 'Войти' : 'Зарегистрироваться'}</button>
      </form>

      <p>
        {isLogin ? 'Нет аккаунта?' : 'Есть аккаунт?'}{' '}
        <button onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? 'Зарегистрироваться' : 'Войти'}
        </button>
      </p>

      {error && <p>{error}</p>}
    </div>
  );
}

export default Profile;
