import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import icon from '../assets/icons/play.png';
import Quiz from './Quiz'; // Импортируем компонент Quiz
import LessonReviews from './LessonReviews'; 
import './LessonDetails.css';

function LessonDetails() {
  const { id } = useParams(); // Получаем ID из URL
  const [lesson, setLesson] = useState(null); // Хранение данных урока
  const [loading, setLoading] = useState(true); // Состояние загрузки
  const [error, setError] = useState(null); // Состояние ошибки
  const [openWords, setOpenWords] = useState({}); // Состояние для открытых слов
  const [likes, setLikes] = useState(0); // Количество лайков
  const [userLiked, setUserLiked] = useState(false); // Проверка, поставил ли пользователь лайк
  const [currentUserId, setCurrentUserId] = useState(null); // ID текущего пользователя
  const [user, setUser] = useState(null); // Данные текущего пользователя (если нужны для других операций)

  // Получение ID текущего пользователя
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decoded = JSON.parse(atob(token.split('.')[1]));
      setCurrentUserId(decoded.userId);
      
      // Получаем данные о текущем пользователе
      axios.get('http://localhost:5000/users/me', {
        headers: { Authorization: `${token}` },
      })
      .then((response) => {
        setUser(response.data); // Сохраняем данные пользователя
      })
      .catch((error) => {
        console.error("Ошибка при получении данных пользователя:", error);
      });
    }
  }, []);

  // Загружаем урок по ID
  useEffect(() => {
    const fetchLesson = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/lessons/${id}`);
        setLesson(response.data); // Сохраняем данные урока в состоянии
        // Устанавливаем количество лайков (если это массив, берём его длину)
        const likeCount = Array.isArray(response.data.likes) ? response.data.likes.length : response.data.likes || 0;
        setLikes(likeCount);

        // Проверяем, лайкал ли пользователь (например, через флаг userLiked)
        setUserLiked(response.data.userLiked || false);
        setLoading(false);
      } catch (err) {
        setError('Не удалось загрузить урок'); // Обработка ошибки
        setLoading(false);
      }
    };

    fetchLesson();
  }, [id]); // useEffect будет вызываться при изменении ID в URL

  const handleWordClick = (index) => {
    setOpenWords((prev) => ({
      ...prev,
      [index]: !prev[index], // Переключаем состояние этого слова
    }));
  };

  const handleDeleteLesson = async () => {
    const token = localStorage.getItem('token');
    if (!token) return alert('Вы не авторизованы!');

    try {
      await axios.delete(`http://localhost:5000/lessons/${id}`, {
        headers: {
          Authorization: `$Bearer {token}`,
        },
      });
      alert('Урок успешно удален!');
      // Перенаправляем на другую страницу, если нужно
    } catch (err) {
      console.error('Ошибка при удалении урока:', err.response?.data || err.message);
      alert('Ошибка при удалении урока');
    }
  };

  const handleLike = async () => {
    if (userLiked) return; // Если пользователь уже лайкал, ничего не делаем

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Пожалуйста, войдите в систему!');
        return;
      }

      const response = await axios.post(`http://localhost:5000/lessons/${id}/like`, {}, {
        headers: { 'Authorization': `${token}` }, // Отправляем токен в заголовке
      });

      if (response.data.message === 'Лайк успешно добавлен') {
        setLikes(response.data.likesCount); // Обновляем количество лайков
        setUserLiked(true); // Отмечаем, что пользователь лайкнул
      }
    } catch (err) {
      console.error('Ошибка при добавлении лайка:', err);
      alert('Ошибка при добавлении лайка');
    }
  };

  if (loading) {
    return <div>Загрузка...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  // Проверка, что lesson не равно null и что у него есть необходимые данные
  if (!lesson) {
    return <div>Урок не найден.</div>;
  }

  // Делаем дополнительные проверки на наличие контента
  const content = lesson.content || {};  // Чтобы избежать ошибки, если content undefined
  const textFragments = content.text_fragments || [];
  const audioFiles = content.audio || [];
  const videoFiles = content.video || [];
  const pictures = content.pictures || [];

  return (
    <div className="lesson-details">
      <div className="lesson-form">
        <h1 className="centered-header">{lesson.title}</h1>
        <p>{lesson.description}</p>
        <p><strong>Язык:</strong> {lesson.language}</p>
        <p><strong>Уровень сложности:</strong> {lesson.difficulty_level}</p>

        <div className="like-section" style={{ marginTop: '20px' }}>
          <button
            onClick={handleLike}
            disabled={userLiked} // Блокируем кнопку, если пользователь уже лайкнул
            style={{
              backgroundColor: userLiked ? '#FF0000' : '#007BFF', // Красный цвет, если лайк поставлен
              color: '#fff',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              cursor: userLiked ? 'not-allowed' : 'pointer',
              marginRight: '10px',
            }}
          >
            {userLiked ? 'Лайк поставлен' : 'Поставить лайк'}
          </button>
          <span className="likes-text">
            {likes} {likes === 1 ? 'лайк' : 'лайков'}
          </span>
        </div>

        {/* Содержание урока */}
        <h2 className="centered-header">Содержание урока</h2>

        {/* Текстовые фрагменты с переводами */}
        <h2 className="centered-header">Текстовые фрагменты</h2>
        {textFragments.length > 0 ? (
          <ul>
            {textFragments.map((item, index) => (
              <li key={index} style={{ marginBottom: '15px' }}>
                <strong>{item.text}</strong>
                <br />
                <em>{item.translation}</em>
              </li>
            ))}
          </ul>
        ) : (
          <p>Нет текстовых фрагментов.</p>
        )}

        <h2 className="centered-header">Аудиофайлы для тренировки наслышанности</h2>
        {audioFiles.length > 0 ? (
          audioFiles.map((audio) => (
            <div key={audio._id}>
              <p>{audio.description}</p>
              <audio controls>
                <source src={`http://localhost:5000/${audio.file}`} type="audio/mpeg" />
                Ваш браузер не поддерживает аудио.
              </audio>
            </div>
          ))
        ) : (
          <p>Нет аудиофайлов.</p>
        )}

        <h2 className="centered-header">Видеоролик</h2>
        {videoFiles.length > 0 ? (
          videoFiles.map((video) => (
            <div key={video._id}>
              <p>{video.description}</p>
              <video width="100%" controls>
                <source src={`http://localhost:5000/${video.file}`} type="video/mp4" />
                Ваш браузер не поддерживает видео.
              </video>
            </div>
          ))
        ) : (
          <p>Нет видеороликов.</p>
        )}

        <h2 className="centered-header">Фотографии</h2>
        {pictures.length > 0 ? (
          pictures.map((picture) => (
            <div key={picture._id}>
              <p>{picture.description}</p>
              <img
                src={`http://localhost:5000/${picture.file}`}
                alt={picture.description}
                style={{ maxWidth: '100%' }}
              />
            </div>
          ))
        ) : (
          <p>Нет изображений.</p>
        )}

        {/* Словарь */}
        <h2 className="centered-header">Ваши новые слова за урок</h2>
        <ul>
          {lesson.vocabulary && lesson.vocabulary.length > 0 ? (
            lesson.vocabulary.map((item, index) => (
              <li key={index} style={{ listStyleType: 'none', display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                <img src={icon} alt="Icon" style={{ width: '20px', height: '20px', marginRight: '10px' }} />
                <div
                  onClick={() => handleWordClick(index)}
                  style={{
                    cursor: 'pointer',
                    display: 'inline-block',
                    backgroundColor: '#f0f0f0',
                    padding: '5px 10px',
                    borderRadius: '5px',
                    margin: '5px',
                    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
                  }}
                >
                  <strong>{item.word}</strong>
                </div>
                {openWords[index] && (
                  <div style={{ marginTop: '5px', paddingLeft: '20px' }}>
                    <em>{item.translation}</em>
                  </div>
                )}
              </li>
            ))
          ) : (
            <li>Нет данных о словах</li>
          )}
        </ul>

        {/* Вставка компонента Quiz */}
        {lesson.quiz && lesson.quiz.length > 0 && <Quiz quizData={lesson.quiz} />}

        <LessonReviews lessonId={id} />
        {lesson && user && lesson.createdBy === user._id && (
          <button onClick={handleDeleteLesson}>Удалить урок</button>
        )}
      </div>
    </div>
  );
}

export default LessonDetails;
