import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom'; // заменили useHistory на useNavigate

function LessonEdit() {
  const { id } = useParams();
  const navigate = useNavigate(); // заменили useHistory на useNavigate
  const [lesson, setLesson] = useState({
    title: '',
    description: '',
    language: '',
    difficulty_level: '',
  });

  useEffect(() => {
    const fetchLesson = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/lessons/${id}`);
        setLesson(response.data);
      } catch (err) {
        console.error("Ошибка при загрузке урока для редактирования:", err);
      }
    };
    fetchLesson();
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLesson({ ...lesson, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Вы не авторизованы');
        return;
      }

      await axios.put(`http://localhost:5000/lessons/${id}`, lesson, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert('Урок успешно обновлен!');
      navigate(`/lessons/${id}`); // использовано navigate вместо history.push
    } catch (err) {
      console.error('Ошибка при обновлении урока:', err);
      alert('Ошибка при обновлении урока');
    }
  };

  return (
    <div className="lesson-edit">
      <h1>Редактировать урок</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Название</label>
          <input
            type="text"
            name="title"
            value={lesson.title}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Описание</label>
          <textarea
            name="description"
            value={lesson.description}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Язык</label>
          <select
            name="language"
            value={lesson.language}
            onChange={handleInputChange}
          >
            <option value="Испанский">Испанский</option>
            <option value="Английский">Английский</option>
          </select>
        </div>
        <div>
          <label>Уровень сложности</label>
          <select
            name="difficulty_level"
            value={lesson.difficulty_level}
            onChange={handleInputChange}
          >
            <option value="Начальный">Начальный</option>
            <option value="Средний">Средний</option>
            <option value="Сложный">Сложный</option>
          </select>
        </div>
        <button type="submit">Сохранить изменения</button>
      </form>
    </div>
  );
}

export default LessonEdit;
