import React, { useEffect, useState } from 'react';
import './LessonList.css'; // Подключение CSS стилей для компонента (если они у вас есть)

const LessonList = () => {
  // Состояния для хранения данных и ошибки
  const [lessons, setLessons] = useState([]);
  const [error, setError] = useState(null);

  // Загружаем уроки при монтировании компонента
  useEffect(() => {
    fetch('http://localhost:5000/lessons') // Адрес API на сервере
      .then((response) => {
        if (!response.ok) {
          throw new Error('Не удалось загрузить уроки');
        }
        return response.json();
      })
      .then((data) => {
        setLessons(data); // Обновляем состояние с уроками
      })
      .catch((error) => {
        setError(error.message); // Если ошибка, устанавливаем её в состояние
      });
  }, []); // Пустой массив зависимостей, чтобы запрос выполнялся только при первом рендере

  return (
    <div className="lesson-list">
      <h1>Список Уроков</h1>

      {/* Если есть ошибка, отображаем её */}
      {error && <p className="error-message">{error}</p>}

      {/* Если уроки успешно загружены, отображаем их */}
      <div className="lesson-cards">
        {lessons.length > 0 ? (
          lessons.map((lesson) => (
            <div key={lesson._id} className="lesson-card">
              <h2>{lesson.title}</h2>
              <p>{lesson.description}</p>
              <p><strong>Язык:</strong> {lesson.language}</p>
              <p><strong>Уровень сложности:</strong> {lesson.difficulty_level}</p>
            </div>
          ))
        ) : (
          <p>Загрузка уроков...</p>
        )}
      </div>
    </div>
  );
};

export default LessonList;
