import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LessonList from './components/LessonList';
import LessonForm from './components/LessonForm';
import LessonDetails from './components/LessonDetails'; // Импортируем компонент для отображения деталей урока
import Navbar from './components/Navbar';
import Profile from './components/Profile';
import MainPage from './components/MainPage'; // Импортируем главный компонент
import './index.css';


function App() {
  return (
    <Router>
      <div className="app-container">
        <Navbar /> {/* Добавляем Navbar сюда, он будет отображаться на всех страницах */}
        <Routes>
          <Route path="/" element={<LessonList />} />
          <Route path="/main" element={<MainPage />} /> {/* Маршрут для главной страницы */}
          <Route path="/create" element={<LessonForm />} />
          <Route path="/lessons/:id" element={<LessonDetails />} /> {/* Маршрут для деталей урока */}
          <Route path="/profile" element={<Profile />} /> {/* Добавляем маршрут для профиля */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
