import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './LessonList.css';

const LessonList = () => {
  const [lessons, setLessons] = useState([]); // Храним все уроки
  const [filteredLessons, setFilteredLessons] = useState([]); // Храним отфильтрованные уроки
  const [error, setError] = useState(null);
  const [languageFilter, setLanguageFilter] = useState(''); // Для фильтрации по языкам
  const [difficultyFilter, setDifficultyFilter] = useState(''); // Для фильтрации по сложности
  const [sortOrder, setSortOrder] = useState(''); // Для сортировки

  const difficultyOrder = {
    'Начальный': 1,
    'Средний': 2,
    'Сложный': 3,
  };

  // Функция для фильтрации и сортировки уроков
  const filterAndSortLessons = () => {
    let filtered = [...lessons];

    // Фильтрация по языку
    if (languageFilter) {
      filtered = filtered.filter((lesson) => lesson.language === languageFilter);
    }

    // Фильтрация по уровню сложности
    if (difficultyFilter) {
      filtered = filtered.filter((lesson) => lesson.difficulty_level === difficultyFilter);
      // Изменение фона страницы в зависимости от уровня сложности
      if (difficultyFilter === 'Начальный') {
        document.body.classList.add('difficulty-easy');
        document.body.classList.remove('difficulty-medium', 'difficulty-hard');
      } else if (difficultyFilter === 'Средний') {
        document.body.classList.add('difficulty-medium');
        document.body.classList.remove('difficulty-easy', 'difficulty-hard');
      } else if (difficultyFilter === 'Сложный') {
        document.body.classList.add('difficulty-hard');
        document.body.classList.remove('difficulty-easy', 'difficulty-medium');
      }
    }

    // Если выбран "Все языки" и "Все уровни", возвращаем светлую тему
    if (!languageFilter && !difficultyFilter) {
      document.body.classList.add('light-theme'); // Добавляем класс светлой темы
      document.body.classList.remove('difficulty-easy', 'difficulty-medium', 'difficulty-hard');
    } else {
      document.body.classList.remove('light-theme'); // Убираем светлую тему
    }

    // Сортировка по уровню сложности
    if (sortOrder) {
      const orderMultiplier = sortOrder === 'asc' ? 1 : -1;
      filtered.sort((a, b) => {
        return (difficultyOrder[a.difficulty_level] - difficultyOrder[b.difficulty_level]) * orderMultiplier;
      });
    }

    setFilteredLessons(filtered); // Обновляем состояние отфильтрованных уроков
  };

  useEffect(() => {
    const fetchLessons = async () => {
      try {
        const response = await fetch('http://localhost:5000/lessons');
        if (!response.ok) {
          throw new Error('Не удалось загрузить уроки');
        }
        const data = await response.json();
        setLessons(data); // Сохраняем все уроки
        setFilteredLessons(data); // Изначально показываем все уроки
      } catch (error) {
        setError(error.message); // Обработка ошибки
      }
    };

    fetchLessons(); // Загружаем уроки
  }, []);

  // Применяем фильтрацию и сортировку при изменении фильтров или порядка сортировки
  useEffect(() => {
    filterAndSortLessons();
  }, [languageFilter, difficultyFilter, sortOrder]); // При изменении фильтров применяем фильтрацию

  return (
    <div className="lesson-list">
      <div className="filter-controls">
        <div className="filter">
          <label htmlFor="language">Язык</label>
          <select
            id="language"
            value={languageFilter}
            onChange={(e) => setLanguageFilter(e.target.value)}
          >
            <option value="">Все языки</option>
            <option value="Испанский">Испанский</option>
            <option value="Английский">Английский</option>
          </select>
        </div>

        <div className="filter">
          <label htmlFor="difficulty">Сложность</label>
          <select
            id="difficulty"
            value={difficultyFilter}
            onChange={(e) => setDifficultyFilter(e.target.value)}
          >
            <option value="">Все уровни</option>
            <option value="Начальный">Начальный</option>
            <option value="Средний">Средний</option>
            <option value="Сложный">Сложный</option>
          </select>
        </div>

        <div className="filter">
          <label htmlFor="sortOrder">Сортировка</label>
          <select
            id="sortOrder"
            value={sortOrder}
            onChange={(e) => setSortOrder(e.target.value)}
          >
            <option value="">Без сортировки</option>
            <option value="asc">От начального к сложному</option>
            <option value="desc">От сложного к начальному</option>
          </select>
        </div>
      </div>

      <div className="lesson-cards">
        <h1>Список Уроков</h1>

        {error && <p className="error-message">{error}</p>}

        {filteredLessons.length > 0 ? (
          filteredLessons.map((lesson) => (
            <Link key={lesson._id} to={`/lessons/${lesson._id}`} className="lesson-card">
              <h2>{lesson.title}</h2>
              <p>{lesson.description}</p>
              <p><strong>Язык:</strong> {lesson.language}</p>
              <p><strong>Уровень сложности:</strong> {lesson.difficulty_level}</p>
            </Link>
          ))
        ) : (
          <p>Нет доступных уроков для выбранных фильтров.</p>
        )}
      </div>
    </div>
  );
};

export default LessonList;
