import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import icon from '../assets/icons/play.png';
import Quiz from './Quiz'; // Импортируем компонент Quiz
import LessonReviews from './LessonReviews'; 
import './LessonDetails.css';

function LessonDetails() {
  const { id } = useParams(); // Получаем ID из URL
  const [lesson, setLesson] = useState(null); // Хранение данных урока
  const [loading, setLoading] = useState(true); // Состояние загрузки
  const [error, setError] = useState(null); // Состояние ошибки
  const [openWords, setOpenWords] = useState({}); // Состояние для открытых слов
  const [likes, setLikes] = useState(0); // Количество лайков
  const [userLiked, setUserLiked] = useState(false); // Проверка, поставил ли пользователь лайк
  const [currentUserId, setCurrentUserId] = useState(null); // ID текущего пользователя
  const [user, setUser] = useState(null); // Данные текущего пользователя (если нужны для других операций)
  const [isEditing, setIsEditing] = useState(false);
  const [editedLesson, setEditedLesson] = useState(null); // Данные для редактирования

  // Состояния для редактирования различных элементов
  const [editedTextFragments, setEditedTextFragments] = useState([]);
  const [editedAudioFiles, setEditedAudioFiles] = useState([]);
  const [editedVideoFiles, setEditedVideoFiles] = useState([]);
  const [editedPictures, setEditedPictures] = useState([]);
  const [editedVocabulary, setEditedVocabulary] = useState([]);

  // Получение ID текущего пользователя
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decoded = JSON.parse(atob(token.split('.')[1]));
      setCurrentUserId(decoded.userId);
      
      // Получаем данные о текущем пользователе
      axios.get('http://localhost:5000/users/me', {
        headers: { Authorization: `${token}` },
      })
      .then((response) => {
        setUser(response.data); // Сохраняем данные пользователя
      })
      .catch((error) => {
        console.error("Ошибка при получении данных пользователя:", error);
      });
    }
  }, []);

  // Загружаем урок по ID
  useEffect(() => {
    const fetchLesson = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/lessons/${id}`);
        setLesson(response.data); // Сохраняем данные урока в состоянии
        const likeCount = Array.isArray(response.data.likes) ? response.data.likes.length : response.data.likes || 0;
        setLikes(likeCount);
        setUserLiked(response.data.userLiked || false);
        setLoading(false);

        // Устанавливаем данные для редактирования
        setEditedLesson(response.data);
        setEditedTextFragments(response.data.content.text_fragments || []);
        setEditedAudioFiles(response.data.content.audio || []);
        setEditedVideoFiles(response.data.content.video || []);
        setEditedPictures(response.data.content.pictures || []);
        setEditedVocabulary(response.data.vocabulary || []);
      } catch (err) {
        setError('Не удалось загрузить урок');
        setLoading(false);
      }
    };

    fetchLesson();
  }, [id]);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleSaveClick = async () => {
    const token = localStorage.getItem('token');
    if (!token) return alert('Вы не авторизованы!');

    try {
      const updatedLesson = {
        ...editedLesson,
        content: {
          ...editedLesson.content,
          text_fragments: editedTextFragments,
          audio: editedAudioFiles,
          video: editedVideoFiles,
          pictures: editedPictures,
        },
        vocabulary: editedVocabulary,
      };

      await axios.put(`http://localhost:5000/lessons/${id}`, updatedLesson, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setLesson(updatedLesson);
      setIsEditing(false);
    } catch (err) {
      console.error('Ошибка при сохранении изменений:', err);
      alert('Ошибка при сохранении изменений');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedLesson((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleTextFragmentChange = (index, newText) => {
    const updatedFragments = [...editedTextFragments];
    updatedFragments[index].text = newText;
    setEditedTextFragments(updatedFragments);
  };

  const handleAudioFileChange = (index, newFile) => {
    const updatedAudio = [...editedAudioFiles];
    updatedAudio[index] = newFile;
    setEditedAudioFiles(updatedAudio);
  };

  const handleVideoFileChange = (index, newFile) => {
    const updatedVideo = [...editedVideoFiles];
    updatedVideo[index] = newFile;
    setEditedVideoFiles(updatedVideo);
  };

  const handlePictureChange = (index, newFile) => {
    const updatedPictures = [...editedPictures];
    updatedPictures[index] = newFile;
    setEditedPictures(updatedPictures);
  };

  const handleVocabularyChange = (index, newWord, newTranslation) => {
    const updatedVocabulary = [...editedVocabulary];
    updatedVocabulary[index] = { word: newWord, translation: newTranslation };
    setEditedVocabulary(updatedVocabulary);
  };

  const handleDeleteLesson = async () => {
    const token = localStorage.getItem('token');
    if (!token) return alert('Вы не авторизованы!');

    try {
      await axios.delete(`http://localhost:5000/lessons/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      alert('Урок успешно удален!');
    } catch (err) {
      console.error('Ошибка при удалении урока:', err.response?.data || err.message);
      alert('Ошибка при удалении урока');
    }
  };

  const handleLike = async () => {
    if (userLiked) return;

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Пожалуйста, войдите в систему!');
        return;
      }

      const response = await axios.post(`http://localhost:5000/lessons/${id}/like`, {}, {
        headers: { 'Authorization': `${token}` },
      });

      if (response.data.message === 'Лайк успешно добавлен') {
        setLikes(response.data.likesCount);
        setUserLiked(true);
      }
    } catch (err) {
      console.error('Ошибка при добавлении лайка:', err);
      alert('Ошибка при добавлении лайка');
    }
  };

  if (loading) {
    return <div>Загрузка...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!lesson) {
    return <div>Урок не найден.</div>;
  }

  const content = lesson.content || {};
  const textFragments = content.text_fragments || [];
  const audioFiles = content.audio || [];
  const videoFiles = content.video || [];
  const pictures = content.pictures || [];

  return (
    <div className="lesson-details">
      <div className="lesson-form">
        <h1 className="centered-header">{lesson.title}</h1>
        <p>{lesson.description}</p>
        {lesson.createdBy === currentUserId && !isEditing && (
          <button onClick={handleEditClick}>Редактировать</button>
        )}
        {isEditing && (
          <div>
            <button onClick={handleSaveClick}>Сохранить изменения</button>
          </div>
        )}

        {/* Содержание урока */}
        <h2 className="centered-header">Содержание урока</h2>

        {/* Текстовые фрагменты с возможностью редактирования */}
        <h2 className="centered-header">Текстовые фрагменты</h2>
        {textFragments.length > 0 ? (
          <ul>
            {editedTextFragments.map((item, index) => (
              <li key={index} style={{ marginBottom: '15px' }}>
                <strong>
                  <textarea
                    value={item.text}
                    onChange={(e) => handleTextFragmentChange(index, e.target.value)}
                    disabled={!isEditing}
                  />
                </strong>
                <br />
                <em>
                  <textarea
                    value={item.translation}
                    onChange={(e) => handleTextFragmentChange(index, e.target.value)}
                    disabled={!isEditing}
                  />
                </em>
              </li>
            ))}
          </ul>
        ) : (
          <p>Нет текстовых фрагментов.</p>
        )}

        {/* Аудиофайлы */}
        <h2 className="centered-header">Аудиофайлы для тренировки наслышанности</h2>
        {audioFiles.length > 0 ? (
          audioFiles.map((audio, index) => (
            <div key={audio._id}>
              <p>{audio.description}</p>
              <audio controls>
                <source src={`http://localhost:5000/${audio.file}`} type="audio/mpeg" />
                Ваш браузер не поддерживает аудио.
              </audio>
              {isEditing && (
                <input type="file" onChange={(e) => handleAudioFileChange(index, e.target.files[0])} />
              )}
            </div>
          ))
        ) : (
          <p>Нет аудиофайлов.</p>
        )}

        {/* Видеофайлы */}
        <h2 className="centered-header">Видеоролик</h2>
        {videoFiles.length > 0 ? (
          videoFiles.map((video, index) => (
            <div key={video._id}>
              <p>{video.description}</p>
              <video width="100%" controls>
                <source src={`http://localhost:5000/${video.file}`} type="video/mp4" />
                Ваш браузер не поддерживает видео.
              </video>
              {isEditing && (
                <input type="file" onChange={(e) => handleVideoFileChange(index, e.target.files[0])} />
              )}
            </div>
          ))
        ) : (
          <p>Нет видеороликов.</p>
        )}

        {/* Фотографии */}
        <h2 className="centered-header">Фотографии</h2>
        {pictures.length > 0 ? (
          pictures.map((picture, index) => (
            <div key={picture._id}>
              <p>{picture.description}</p>
              <img
                src={`http://localhost:5000/${picture.file}`}
                alt={picture.description}
                style={{ maxWidth: '100%' }}
              />
              {isEditing && (
                <input type="file" onChange={(e) => handlePictureChange(index, e.target.files[0])} />
              )}
            </div>
          ))
        ) : (
          <p>Нет изображений.</p>
        )}

        {/* Словарь */}
        <h2 className="centered-header">Ваши новые слова за урок</h2>
        <ul>
          {editedVocabulary.length > 0 ? (
            editedVocabulary.map((item, index) => (
              <li key={index} style={{ listStyleType: 'none', display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                <img src={icon} alt="Icon" style={{ width: '20px', height: '20px', marginRight: '10px' }} />
                <div
                  style={{
                    cursor: 'pointer',
                    display: 'inline-block',
                    backgroundColor: '#f0f0f0',
                    padding: '5px 10px',
                    borderRadius: '5px',
                    margin: '5px',
                    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
                  }}
                >
                  <input
                    value={item.word}
                    onChange={(e) => handleVocabularyChange(index, e.target.value, item.translation)}
                    disabled={!isEditing}
                  />
                  <input
                    value={item.translation}
                    onChange={(e) => handleVocabularyChange(index, item.word, e.target.value)}
                    disabled={!isEditing}
                  />
                </div>
              </li>
            ))
          ) : (
            <li>Нет данных о словах</li>
          )}
        </ul>

        {/* Вставка компонента Quiz */}
        {lesson.quiz && lesson.quiz.length > 0 && <Quiz quizData={lesson.quiz} />}

        <LessonReviews lessonId={id} />
        {lesson && user && lesson.createdBy === user._id && (
          <button onClick={handleDeleteLesson}>Удалить урок</button>
        )}
      </div>
    </div>
  );
}

export default LessonDetails;
