import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Quiz from './Quiz';
import LessonReviews from './LessonReviews';
import LessonEdit from './LessonEdit';
import './LessonDetails.css';

function LessonDetails() {
  const { id } = useParams();
  const [lesson, setLesson] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [likes, setLikes] = useState(0);
  const [userLiked, setUserLiked] = useState(false);
  const [currentUserId, setCurrentUserId] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decoded = JSON.parse(atob(token.split('.')[1]));
      setCurrentUserId(decoded.userId);
    }
  }, []);

  useEffect(() => {
    const fetchLesson = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/lessons/${id}`);
        setLesson(response.data);
        setLikes(response.data.likes.length || 0);
        setUserLiked(response.data.userLiked || false);
        setLoading(false);
      } catch (err) {
        setError('Не удалось загрузить урок');
        setLoading(false);
      }
    };

    fetchLesson();
  }, [id]);

  const handleLike = async () => {
    if (userLiked) return;

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Пожалуйста, войдите в систему!');
        return;
      }

      const response = await axios.post(`http://localhost:5000/lessons/${id}/like`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data.message === 'Лайк успешно добавлен') {
        setLikes(response.data.likesCount);
        setUserLiked(true);
      }
    } catch (err) {
      console.error('Ошибка при добавлении лайка:', err);
      alert('Ошибка при добавлении лайка');
    }
  };

  if (loading) return <div>Загрузка...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="lesson-details">
      <h1>{lesson.title}</h1>
      <p>{lesson.description}</p>
      {lesson.createdBy === currentUserId && !isEditing && (
        <button onClick={() => setIsEditing(true)}>Редактировать</button>
      )}
      {isEditing && (
        <LessonEdit
          lesson={lesson}
          onSave={(updatedLesson) => {
            setLesson(updatedLesson);
            setIsEditing(false);
          }}
          onCancel={() => setIsEditing(false)}
        />
      )}
      {!isEditing && (
        <>
          <h2>Содержание</h2>
          {lesson.content?.text_fragments?.map((fragment, index) => (
            <p key={index}>{fragment.text}</p>
          ))}
          {lesson.content?.audio?.map((audio, index) => (
            <div key={index}>
              <audio controls>
                <source src={`http://localhost:5000/${audio.file}`} type="audio/mpeg" />
              </audio>
            </div>
          ))}
          {lesson.content?.video?.map((video, index) => (
            <div key={index}>
              <video width="100%" controls>
                <source src={`http://localhost:5000/${video.file}`} type="video/mp4" />
              </video>
            </div>
          ))}
          {lesson.content?.pictures?.map((picture, index) => (
            <img
              key={index}
              src={`http://localhost:5000/${picture.file}`}
              alt={picture.description}
              style={{ maxWidth: '100%' }}
            />
          ))}
          <Quiz quizData={lesson.quiz} />
          <LessonReviews lessonId={id} />
          <button onClick={handleLike}>
            {userLiked ? 'Вы уже поставили лайк' : `Лайк (${likes})`}
          </button>
        </>
      )}
    </div>
  );
}

export default LessonDetails;
