import React, { useState } from 'react';
import axios from 'axios';

function LessonEdit({ lesson, onSave, onCancel }) {
  const [editedLesson, setEditedLesson] = useState(lesson);
  const [editedTextFragments, setEditedTextFragments] = useState(lesson.content?.text_fragments || []);
  const [editedAudioFiles, setEditedAudioFiles] = useState(lesson.content?.audio || []);
  const [editedVideoFiles, setEditedVideoFiles] = useState(lesson.content?.video || []);
  const [editedPictures, setEditedPictures] = useState(lesson.content?.pictures || []);
  const [editedVocabulary, setEditedVocabulary] = useState(lesson.vocabulary || []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedLesson((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSave = async () => {
    const token = localStorage.getItem('token');
    if (!token) return alert('Вы не авторизованы!');

    try {
      const updatedLesson = {
        ...editedLesson,
        content: {
          ...editedLesson.content,
          text_fragments: editedTextFragments,
          audio: editedAudioFiles,
          video: editedVideoFiles,
          pictures: editedPictures,
        },
        vocabulary: editedVocabulary,
      };

      await axios.put(`http://localhost:5000/lessons/${lesson._id}`, updatedLesson, {
        headers: { Authorization: `Bearer ${token}` },
      });

      onSave(updatedLesson);
    } catch (err) {
      console.error('Ошибка при сохранении изменений:', err);
      alert('Ошибка при сохранении изменений');
    }
  };

  const handleFileChange = (type, index, file) => {
    const updater = {
      audio: setEditedAudioFiles,
      video: setEditedVideoFiles,
      pictures: setEditedPictures,
    }[type];

    const updated = [...editedLesson.content[type]];
    updated[index] = { ...updated[index], file };
    updater(updated);
  };

  return (
    <div>
      <h2>Редактирование урока</h2>
      <label>Название урока:</label>
      <input
        name="title"
        value={editedLesson.title}
        onChange={handleInputChange}
      />
      <h3>Текстовые фрагменты</h3>
      {editedTextFragments.map((fragment, index) => (
        <div key={index}>
          <textarea
            value={fragment.text}
            onChange={(e) =>
              setEditedTextFragments((prev) =>
                prev.map((f, i) => (i === index ? { ...f, text: e.target.value } : f))
              )
            }
          />
        </div>
      ))}
      <h3>Аудио</h3>
      {editedAudioFiles.map((audio, index) => (
        <div key={index}>
          <input type="file" onChange={(e) => handleFileChange('audio', index, e.target.files[0])} />
        </div>
      ))}
      <h3>Видео</h3>
      {editedVideoFiles.map((video, index) => (
        <div key={index}>
          <input type="file" onChange={(e) => handleFileChange('video', index, e.target.files[0])} />
        </div>
      ))}
      <h3>Фотографии</h3>
      {editedPictures.map((picture, index) => (
        <div key={index}>
          <input type="file" onChange={(e) => handleFileChange('pictures', index, e.target.files[0])} />
        </div>
      ))}
      <h3>Словарь</h3>
      {editedVocabulary.map((word, index) => (
        <div key={index}>
          <input
            value={word.word}
            onChange={(e) =>
              setEditedVocabulary((prev) =>
                prev.map((w, i) => (i === index ? { ...w, word: e.target.value } : w))
              )
            }
          />
          <input
            value={word.translation}
            onChange={(e) =>
              setEditedVocabulary((prev) =>
                prev.map((w, i) => (i === index ? { ...w, translation: e.target.value } : w))
              )
            }
          />
        </div>
      ))}
      <button onClick={handleSave}>Сохранить</button>
      <button onClick={onCancel}>Отмена</button>
    </div>
  );
}

export default LessonEdit;
