import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './LessonList.css';

function LessonList() {
  const [lessons, setLessons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Запрос данных с backend
    const fetchLessons = async () => {
      try {
        const response = await axios.get('http://localhost:5000/lessons');
        setLessons(response.data);
      } catch (err) {
        setError('Ошибка при загрузке уроков.');
      } finally {
        setLoading(false);
      }
    };

    fetchLessons();
  }, []);

  if (loading) return <p>Загрузка...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="lesson-list">
      <h1>Список уроков</h1>
      <div className="lesson-cards">
        {lessons.map((lesson) => (
          <div key={lesson._id} className="lesson-card">
            <h2>{lesson.title}</h2>
            <p>{lesson.description}</p>
            <p><strong>Язык:</strong> {lesson.language}</p>
            <p><strong>Уровень сложности:</strong> {lesson.difficulty_level}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default LessonList;
