import React, { useState } from 'react';
import './Quiz.css'; // Импортируем стили

const Quiz = ({ quizData }) => {
  const [quizAnswers, setQuizAnswers] = useState({});
  const [isQuizSubmitted, setIsQuizSubmitted] = useState(false);

  // Обработка выбора ответа
  const handleQuizAnswer = (questionIndex, optionIndex) => {
    if (!isQuizSubmitted) {
      setQuizAnswers((prev) => ({
        ...prev,
        [questionIndex]: optionIndex,
      }));
    }
  };

  // Отправка теста
  const handleSubmitQuiz = () => {
    setIsQuizSubmitted(true); // Блокируем возможность изменения ответов после отправки
  };

  return (
    <div className="quiz-container">
      <h2>Небольшой тест для проверки знаний</h2>
      {quizData && quizData.length > 0 ? (
        quizData.map((item, questionIndex) => (
          <div key={questionIndex} className="quiz-question">
            <strong>{item.question}</strong>
            <ul>
              {item.options.map((option, optionIndex) => {
                const isCorrect = optionIndex === item.correct_answer;
                const isSelected = quizAnswers[questionIndex] === optionIndex;

                let buttonStyle = {}; // Начальный стиль

                if (isQuizSubmitted) {
                  // Если тест отправлен, проверяем правильность выбора
                  if (isSelected) {
                    buttonStyle = isCorrect
                      ? { backgroundColor: 'green', color: 'white' }  // Правильный ответ
                      : { backgroundColor: 'red', color: 'white' };  // Неправильный ответ
                  } else if (isCorrect) {
                    buttonStyle = { backgroundColor: 'green', color: 'white' };  // Правильный ответ, который не был выбран
                  } else {
                    buttonStyle = { backgroundColor: 'white', color: 'black' }; // Неправильный, не выбранный ответ
                  }
                } else {
                  // Стиль по умолчанию до отправки теста (синие кнопки)
                  buttonStyle = { backgroundColor: '#007bff', color: 'white' };
                }

                return (
                  <li key={optionIndex} className="quiz-option">
                    <button
                      onClick={() => handleQuizAnswer(questionIndex, optionIndex)}
                      style={{
                        padding: '10px',
                        margin: '5px',
                        cursor: 'pointer',
                        borderRadius: '5px',
                        ...buttonStyle,
                      }}
                      disabled={isQuizSubmitted} // Заблокировать кнопки после отправки
                    >
                      {option}
                    </button>
                  </li>
                );
              })}
            </ul>
          </div>
        ))
      ) : (
        <div>Нет данных о тестах</div>
      )}
      {!isQuizSubmitted && (
        <button
          onClick={handleSubmitQuiz}
          className="submit-quiz-btn"
        >
          Отправить тест
        </button>
      )}
    </div>
  );
};

export default Quiz;
