import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Profile() {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true);
  const [successMessage, setSuccessMessage] = useState('');
  const [showLessonForm, setShowLessonForm] = useState(false);
  const [lessonData, setLessonData] = useState({
    title: '',
    description: '',
    language: '',
    difficulty_level: '',
    content: {
      text_fragments: [{ text: '', translation: '' }],
      audio: [{ file: null, description: '' }],
      video: [{ file: null, description: '' }],
      pictures: [{ file: null, description: '' }],
    },
    vocabulary: [{ word: '', translation: '' }],
    quiz: [{ question: '', options: ['', '', '', ''], correct_answer: 0 }],
  });
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchProfile(token);
    }
  }, []);

  const fetchProfile = async (token) => {
    try {
      const response = await axios.get('http://localhost:5000/profile', {
        headers: {
          Authorization: token,
        },
      });
      setUser(response.data);
    } catch (err) {
      console.error('Ошибка при загрузке профиля:', err.response?.data || err.message);
      setError('Ошибка при загрузке данных профиля');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/login', { email, password });
      const { token } = response.data;
      localStorage.setItem('token', token);
      setSuccessMessage('Ура, вы успешно вошли!');
      setTimeout(() => {
        navigate('/profile');
      }, 2000);
    } catch (err) {
      setError('Неверный логин или пароль');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setSuccessMessage('');
  };

  const handleCreateLesson = () => {
    setShowLessonForm(true);
  };

  const handleSaveLesson = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const formData = new FormData();

      formData.append('title', lessonData.title);
      formData.append('description', lessonData.description);
      formData.append('language', lessonData.language);
      formData.append('difficulty_level', lessonData.difficulty_level);

      // Добавление контента
      lessonData.content.audio.forEach((audio, i) => {
        if (audio.file) formData.append(`audio[${i}][file]`, audio.file);
        formData.append(`audio[${i}][description]`, audio.description);
      });

      lessonData.content.video.forEach((video, i) => {
        if (video.file) formData.append(`video[${i}][file]`, video.file);
        formData.append(`video[${i}][description]`, video.description);
      });

      lessonData.content.pictures.forEach((picture, i) => {
        if (picture.file) formData.append(`pictures[${i}][file]`, picture.file);
        formData.append(`pictures[${i}][description]`, picture.description);
      });

      lessonData.content.text_fragments.forEach((fragment, i) => {
        formData.append(`text_fragments[${i}][text]`, fragment.text);
        formData.append(`text_fragments[${i}][translation]`, fragment.translation);
      });

      lessonData.vocabulary.forEach((word, i) => {
        formData.append(`vocabulary[${i}][word]`, word.word);
        formData.append(`vocabulary[${i}][translation]`, word.translation);
      });

      lessonData.quiz.forEach((question, i) => {
        formData.append(`quiz[${i}][question]`, question.question);
        question.options.forEach((option, j) => {
          formData.append(`quiz[${i}][options][${j}]`, option);
        });
        formData.append(`quiz[${i}][correct_answer]`, question.correct_answer);
      });

      await axios.post('http://localhost:5000/lessons', formData, {
        headers: {
          Authorization: token,
          'Content-Type': 'multipart/form-data',
        },
      });

      alert('Урок успешно создан!');
      setLessonData({
        title: '',
        description: '',
        language: '',
        difficulty_level: '',
        content: {
          text_fragments: [{ text: '', translation: '' }],
          audio: [{ file: null, description: '' }],
          video: [{ file: null, description: '' }],
          pictures: [{ file: null, description: '' }],
        },
        vocabulary: [{ word: '', translation: '' }],
        quiz: [{ question: '', options: ['', '', '', ''], correct_answer: 0 }],
      });
      setShowLessonForm(false);
    } catch (err) {
      console.error('Ошибка при создании урока:', err.response?.data || err.message);
      alert('Ошибка при создании урока');
    }
  };

  const handleLessonInputChange = (e) => {
    const { name, value } = e.target;
    setLessonData({ ...lessonData, [name]: value });
  };

  const handleInputChangeInArray = (path, index, field, value) => {
    const keys = path.split('.');
    const newLessonData = { ...lessonData };
    let target = newLessonData;

    keys.forEach((key, i) => {
      if (i === keys.length - 1) {
        if (Array.isArray(target[key])) {
          if (field) {
            target[key][index][field] = value;
          } else {
            target[key][index] = value;
          }
        }
      } else {
        target = target[key];
      }
    });

    setLessonData(newLessonData);
  };

  const handleAddItem = (path) => {
    const keys = path.split('.');
    const newLessonData = { ...lessonData };
    let target = newLessonData;

    keys.forEach((key, i) => {
      if (i === keys.length - 1) {
        if (Array.isArray(target[key])) {
          target[key].push({});
        }
      } else {
        target = target[key];
      }
    });

    setLessonData(newLessonData);
  };

  const handleRemoveItem = (path, index) => {
    const keys = path.split('.');
    const newLessonData = { ...lessonData };
    let target = newLessonData;

    keys.forEach((key, i) => {
      if (i === keys.length - 1) {
        if (Array.isArray(target[key])) {
          target[key].splice(index, 1);
        }
      } else {
        target = target[key];
      }
    });

    setLessonData(newLessonData);
  };

  if (user) {
    return (
      <div>
        <h1>Добро пожаловать, {user.name || 'Пользователь'}</h1>
        <p>Email: {user.email}</p>
        <p>Дата регистрации: {user.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'Неизвестно'}</p>
        <button onClick={handleLogout}>Выйти</button>
        <button onClick={handleCreateLesson}>Создать урок</button>

        {showLessonForm && (
          <form onSubmit={handleSaveLesson}>
            {/* Добавьте весь JSX для формы создания урока */}
          </form>
        )}
      </div>
    );
  }

  return (
    <div>
      <h1>{isLogin ? 'Вход' : 'Регистрация'}</h1>
      <form onSubmit={isLogin ? handleLogin : () => {}}>
        <div>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">{isLogin ? 'Войти' : 'Зарегистрироваться'}</button>
      </form>
      <p>
        {isLogin ? 'Нет аккаунта?' : 'Есть аккаунт?'}{' '}
        <button onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? 'Зарегистрироваться' : 'Войти'}
        </button>
      </p>
      {successMessage && <p>{successMessage}</p>}
      {error && <p>{error}</p>}
    </div>
  );
}

export default Profile;
